import React, { Component } from "react";
import axios from "axios";
import Nav from "react-bootstrap/Nav";
import Form from "react-bootstrap/Form";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";


export default class VendorViewAll extends Component {
  constructor(props) {
      super(props);
      this.state = { prods: [] };
      this.deleteprod=this.deleteprod.bind(this);
    }

    componentDidMount() {
      const newUser = {
        username: localStorage.getItem("username")
      };
      // this.setState({ username: newUser.username });
      axios
        .post("http://localhost:4000/viewVendorProduct", newUser)
        .then(response => {
          this.setState({ prods: response.data });
        })
        .catch(function(error) {
          console.log(error);
        });
    }
  deleteprod(prod_id) {
    console.log(prod_id)
    const newProd = {
      id: prod_id
    };
    axios
      .post("http://localhost:4000/deleteVendorProduct", newProd)
      .then(response => {
        // this.setState({ prods: response.data });
        console.log("Deleted")
      })
      .catch(function(error) {
        console.log(error);
      });
    const newUser = {
      username: localStorage.getItem("username")
      };
    axios
      .post("http://localhost:4000/viewVendorProduct", newUser)
      .then(response => {
        this.setState({ prods: response.data });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <Nav variant="pills" defaultActiveKey="/home">
          <Nav.Item>
            <Nav.Link href="/VendorHome">HOME</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/VendorAddProduct">Add Products</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/VendorViewProduct">View All</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link href="/dispatch">Dispatch</Nav.Link>
          </Nav.Item>
        </Nav>
        <br/>
        <br/>

        <h1>{localStorage.getItem("username")}'s Products</h1>

        <table className="table table-striped">
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Quantity Ordered</th>
              <th>Quantity Remaining</th>
              <th>Status</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {this.state.prods.map((p, i) => {
              return (
                <tr>
                  <td>{p.productname}</td>
                  <td>{p.price}</td>
                  <td>{p.quantity}</td>
                  <td>{p.quantity_ordered}</td>
                  <td>{p.quantity_remaining}</td>
                  <td>{p.status}</td>
                  <td className="del-cell">
                  <input type="button" value="X" onClick={()=>{
                    this.deleteprod(p._id);
                  }} className="del-btn"/>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}
